import java.util.ArrayList;

public class CosmicVectorAngleSolver {

    /**
     * Calculates the angle in degrees between two vectors represented as ArrayLists of Double.
     * 
     * @param vector1 The first vector.
     * @param vector2 The second vector.
     * @return The angle between the two vectors in degrees.
     */
    public static double calculateCosmicAngle(ArrayList<Double> vector1, ArrayList<Double> vector2) {
        double dotProduct = 0.0;
        double magnitudeOfVector1 = 0.0;
        double magnitudeOfVector2 = 0.0;

        // Calculate the dot product
        for (int i = 0; i < vector1.size(); i++) {
            dotProduct += vector1.get(i) * vector2.get(i);
        }

        // Calculate the magnitudes
        for (double component : vector1) {
            magnitudeOfVector1 += component * component;
        }
        for (double component : vector2) {
            magnitudeOfVector2 += component * component;
        }
        magnitudeOfVector1 = Math.sqrt(magnitudeOfVector1);
        magnitudeOfVector2 = Math.sqrt(magnitudeOfVector2);

        // Calculate the angle in radians and then convert it to degrees
        double angleInRadians = Math.acos(dotProduct / (magnitudeOfVector1 * magnitudeOfVector2));
        double angleInDegrees = Math.toDegrees(angleInRadians);

        return angleInDegrees;
    }

    public static void main(String[] args) {
        ArrayList<Double> vector1 = new ArrayList<Double>();
        vector1.add(0.6);
        vector1.add(0.8);
        vector1.add(0.2);

        ArrayList<Double> vector2 = new ArrayList<Double>();
        vector2.add(0.5);
        vector2.add(0.1);
        vector2.add(0.9);

        double angle = calculateCosmicAngle(vector1, vector2);
        System.out.println("The angle between the two vectors is: " + angle + " degrees");
    }
}
